def format_breed_name(breed_name):
    return breed_name.replace('_', ' ').title()

def get_pet_features(breed_name):
    for breed in breed_data:
        if breed['breed_name'] == breed_name:
            return {
                'pet_species': 'Dog' if breed['pet_type'] == 1 else 'Cat',
                'pet_temperament': breed['temperament'],
                'pet_activity_level': breed['activity_level'],
                'pet_care_needs': breed['care_needs'],
                'pet_noise_level': breed['noise_level'],
                'pet_good_with_kids': breed['good_with_kids'],
                'pet_good_with_pets': breed['good_with_pets']
            }
    raise ValueError(f"Breed {breed_name} not found")


def get_unformated_breed_name(name):
    for breed in breed_data:
        if breed['breed_name_ro'] == name:
            return breed['breed_name']
        if format_breed_name(breed['breed_name']) == name:
            return breed['breed_name']
    return None

# Example usage
pet_features = get_pet_features('beagle')
print(pet_features)